const input = require("readline-sync")
const ano = input.question("Digite o ano de nascimento entre 1922 e 2021 ");

// Função para ler um ano de nascimento válido
function lerAnoDeNascimento(){
 
    if (!isNaN(ano) && ano >= 1922 && ano <= 2021){
      console.log ("ano");
    } else{
      console.log("Ano de nascimento inválido. Tente novamente.");
    }
}
  

// Função principal do programa
function main(){
  const nomeCompleto = input.question("Digite o seu nome completo: ");
  const anoNascimento = lerAnoDeNascimento();

  const anoAtual = 2022;
  const idade = anoAtual - anoNascimento;

  console.log(`Nome: ${nomeCompleto}`);
  console.log(`Idade: ${idade} anos`);
}

// Chama a função principal para executar o programa
lerAnoDeNascimento();
